package ejerciciosRepetitiva;

public class Ejercicio3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// imprime por pantalla los 20 primeros números pares positivos
		int numPares = 0, cuentaPares = 0;

		while (cuentaPares < 20) {
			System.out.println(numPares);
			numPares = numPares + 2;
			cuentaPares++;
		}

	}

}
