public class Ejercicio7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Programa que te da 7 y sus potencias 2 y 3
		int pot2 = 0, pot3 = 0;

		pot2 = 7 * 7;
		pot3 = 7 * 7 * 7;
		System.out.println("El número es 7");
		System.out.println("El número elevado a 2 es " + pot2);
		System.out.println("El número elevado a 3 es " + pot3);
	}

}
